int main (void)
{

// Declaring a DH session
DH *dh_session;

// Retrieve it from the code copied above
dh_session = get_dh2048();

// Generate the public and private key pair
DH_generate_key(dh_session);

/*
 * Here you should:
 * 1) convert your pk from BN to bin
 * 2) send your pk_bin to the other party
 */


// Receive the public key from the other party.
// In this example we make only a conversion from an hard coded value
BIGNUM *recv_pk = NULL;
BN_dec2bn(&recv_pk, "01234567890123456789012345678901234567890123456789");

// Compute the shared key
unsigned char *sharedkey;
int sharedkey_size;
sharedkey = malloc(sizeof(unsigned char) * (DH_size(dh_session)));

sharedkey_size = DH_compute_key(sharedkey, recv_pk, dh_session);

// Do something with the shared key 
// Note sharedkey_size may be less than DH_size(dh_session)
printf("The shared key is:\n");
BIO_dump_fp(stdout, sharedkey, sharedkey_size);

// Clean up
free(sharedkey);
BN_free(recv_pk);
DH_free(dh_session);
return 0;
}
